local ITEM = {}ITEM.DataName = "gun__base"function ITEM:AddOptionsUse(menu, item)	if item.Item:GetData().Slots then		local slots = item.Item:GetData().Slots					if slots[1] or slots[2] then			local slot = menu:AddSubMenu("Remove Chip From Slot:")							if slots[1] then				slot:AddOption("Slot 1", function()					surface.PlaySound("UI/buttonclick.wav")					RunConsoleCommand("removeechip", item.Item:GetIDRef(), 1)				end)			end							if slots[2] then					slot:AddOption("Slot 2", function()					surface.PlaySound("UI/buttonclick.wav")					RunConsoleCommand("removeechip", item.Item:GetIDRef(), 2)				end)			end		end	endendRegisterItem(ITEM)