local ITEM = {}ITEM.DataName = "gun__base"function ITEM:OnItemCreation()	self.Slots = {}endfunction ITEM:DestroyChip(pl, plitem, slot)	if plitem:GetData().Slots then		if plitem:GetData().Slots[slot] then			plitem:GetData().Slots[slot] = nil			pl:SendInventory()			for _, wep in pairs(pl:GetWeapons()) do				if wep.GetItemID then					if wep:GetItemID() == plitem:GetIDRef() then						wep:UpdateEnhancements()					end				end			end		end	endendfunction ITEM:ItemCallback(pl, plitem, args)	local slot = tonumber(args[2])	if slot then		if slot <= 2 then			self:DestroyChip(pl, plitem, slot)		end	endendfunction ITEM:SetWeaponStats(stats)	self.WeaponStats = statsendfunction ITEM:RefreshWeaponStats()	if not self.WeaponStats then		self.WeaponStats = {}	end	self.WeaponStats["Damage"] = self.Damage	self.WeaponStats["ClipSize"] = self.ClipSize	self.WeaponStats["NumShots"] = self.NumShots	self.WeaponStats["Delay"] = self.Delay	self.WeaponStats["Cone"] = self.Coneendfunction ITEM:SetSpecificWeaponStat(stat, var)	if not self.WeaponStats then		self.WeaponStats = {}	end	self.WeaponStats[stat] = varendRegisterItem(ITEM)