local WRECIPE = {}WRECIPE.RequiredReagents = {	["gasoline"] = 1,	["sugar"] = 1}function WRECIPE:BuildRecipe(entity, scale)	local effect = EffectData()		effect:SetOrigin(entity:GetPos())	util.Effect("firegrenade", effect)					entity:EmitSound("weapons/smokegrenade/sg_explode.wav")					local firefield = ents.Create("spot_incendiary")	if firefield then		firefield:SetPos(entity:GetPos())		firefield:SetOwner(entity:GetOwner())		firefield:Spawn()	endendRegisterChemistryRecipe(WRECIPE)