local WRECIPE = {}WRECIPE.IgnoreSelfReagents = trueWRECIPE.RequiredReagents = {	["water"] = 1, 	["ash"] = 1}function WRECIPE:BuildRecipe(entity, scale, useditems, itemsleft)	local infosmoke = ents.Create("point_smoke")		infosmoke:SetPos(entity:GetPos())		infosmoke:Spawn()				infosmoke.Reagents = itemsleft		infosmoke:StartSmoke(scale)				//Remove all of the reagents left since the smoke takes them		entity.Data:EmptyItems()	--[[	local pointsmoke = ents.Create("point_smoke")		pointsmoke:SetPos(entity:GetPos())		pointsmoke:Spawn()				pointsmoke:CreateSmoke(scale)]]endRegisterChemistryRecipe(WRECIPE)