local WRECIPE = {}WRECIPE.RequiredReagents = {	["water_boiling"] = 10, 	["sugar"] = 10}function WRECIPE:IsInTemperatureRange(ent)	if not ent:IsValid() then return false end	if ent.GetTemperature then		return ent:GetTemperature() >= 300	else		return false	endendfunction WRECIPE:BuildRecipe(entity, scale)	local item = Item("steroids")		item:SetAmount(5)			entity.Data:AddItem(item)endRegisterChemistryRecipe(WRECIPE)